package Loops;

public class MySwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String day="one";
		switch(day)
		{
		case "one":
		{
			System.out.println("Monday");
			break;
		}
		case "two":
		{
			System.out.println("Tuesday");
			break;
		}
		default:
		{
			System.out.println("Wednesday");
			break;
		}
		
	}
}
}
